import * as React from 'react';
import { useState, useEffect } from 'react';
// import { dummy } from '../test/example.js';
// import './Login.css';
import { db } from '../test/firebase';
import { doc, getDoc, onSnapshot } from 'firebase/firestore';

import Dialog from '@mui/material/Dialog';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import Slide from '@mui/material/Slide';

import Grid from '@mui/material/Grid2';
import { Box, Typography, Button, ButtonGroup } from '@mui/material';
import { Margin } from '@mui/icons-material';

const docRef = doc(db, "test", "room");

const roomName = { 'A': '알파', 'B': '베타', 'C': '감마' };
const roomState = { 0: '예약 가능', 1: '예약중', 2: '예약 불가' };
const days = ['day1', 'day2', 'day3'];

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function TablePage({ user, setTablePageOpen, tablePageOpen, setReserveOpen }) {
  const [selectedDay, setSelectedDay] = useState('day1'); // 초기 선택된 날짜는 'day1'
  const [timetable, setTimetable] = useState(''); // 초기 선택된 날짜는 'day1'

  async function getTimetable() {
    const docSnap = await getDoc(docRef);

    setTimetable(docSnap.data());
  };

  useEffect(() => {
    getTimetable();

    const unsubscribe_TimeTable = onSnapshot(docRef, (doc) => {
      setTimetable(doc.data());
    });
    // 컴포넌트 언마운트 시 리스너 정리
    return () => unsubscribe_TimeTable();
  }, []);

  const handleDayChange = (day) => {
    setSelectedDay(day);
  };
  const handleClose = () => {
    setTablePageOpen(false);
  };

  const times = Array.from({ length: 36 }, (_, i) => {
    const hour = String(Math.floor(6 + i / 2)).padStart(2, '0');
    const minute = i % 2 === 0 ? '00' : '30';
    return `${hour}:${minute}`;
  });

  return (
    <React.Fragment>
      <Dialog
        fullScreen
        open={tablePageOpen}
        onClose={handleClose}
        TransitionComponent={Transition}
      >
        <AppBar sx={{ position: 'relative', bgcolor: '#092979' }}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleClose}
              aria-label="close"
            >
              <CloseIcon />
            </IconButton>
            <Typography sx={{ ml: 2, flex: 1 }} variant="h6" component="div">
              예약하기
            </Typography>
          </Toolbar>
          <Box sx={{
            flexGrow: 1, padding: 1, display: 'flex',
            flexDirection: 'row', alignItems: 'start'
          }}>
            <ButtonGroup variant="contained">

              {days.map(day => (
                <Button
                  key={day}
                  sx={{
                    bgcolor: selectedDay === day ? 'white' : '#092979',
                    color: selectedDay === day ? '#092979' : 'white',
                    borderRadius: 2
                  }}
                  onClick={() => handleDayChange(day)}              >
                  {`Day ${day.charAt(3)}`} {/* day1 => 1, day2 => 2 형식으로 표시 */}
                </Button>
              ))}
            </ButtonGroup>
          </Box>
        </AppBar>

        {/* 열 */}
        <Box sx={{ flexGrow: 1, paddingTop: '15px', display: 'flex', bgcolor: '#f6f7f8' }}>
          <Grid direction="row" container spacing={0.5} justifyContent="center" alignItems="center" sx={{ width: '100%' }}>
            <Grid sx={{ width: '60px' }}>
              <Typography variant="h6" align="center" sx={{ bgcolor: 'white', borderBottom: '1px solid #ddd' }}>
                시간
              </Typography>
            </Grid>
            {Object.entries(timetable).filter(([key]) => ['A', 'B', 'C'].includes(key)).sort(([keyA], [keyB]) => keyA.localeCompare(keyB)).map(([key, days]) => (
              <Grid sx={{ width: '25%' }} key={key}>
                <Typography variant="h6" align="center" sx={{ bgcolor: 'white', borderBottom: '1px solid #ddd', }}>
                  {roomName[key]}
                </Typography>
              </Grid>))}
          </Grid>
        </Box>

        {/* 데이터 */}
        <Box
          sx={{
            paddingTop: '5px',
            paddingBottom: '60px',
            bgcolor: '#f6f7f8',
            maxHeight: '100%',
            overflowY: 'auto',
            overflowX: 'auto',
            maxWidth: '100%',
          }}
        >
          <Grid container spacing={0.5} justifyContent="flex-start" alignItems="center">
            {/* 시간 열 */}
            <Grid  sx={{ width: '50px', flexWrap: 'nowrap' }}>
              {times.map((time) => (
                <Box key={time} sx={{ padding: 1, borderBottom: '1px solid #ddd' }}>
                  <Typography align="center">{time}</Typography>
                </Box>
              ))}
            </Grid>

            {/* 그룹별 상태 열 */}
            {Object.entries(timetable)
              .filter(([key]) => ['A', 'B', 'C'].includes(key))
              .sort(([keyA], [keyB]) => keyA.localeCompare(keyB))
              .map(([key, days]) => (
                <Grid
                  key={key}
                  justifyContent="flex-start"
                  alignItems="center"
                  sx={{ width: 'auto', flexShrink: 0 }} // 여기에 flexShrink: 0을 추가하여 아이템이 늘어나는 것을 방지
                >
                  {days.Reserve[selectedDay].map((status, index) => (
                    <Box
                      key={index}
                      onClick={status ? null : () => setReserveOpen(true)}
                      sx={{
                        Margin: 2,
                        padding: 1,
                        borderBottom: '1px solid #ddd',
                        backgroundColor: status === 0 ? 'white' : '#eef2ff',
                        color: status === 0 ? '#3e5ba5' : status === 2 ? '#724c50' : '#888c96',
                        width: '100px',  // 적절한 너비 지정 (여기서 필요에 맞게 조정)
                        display: 'inline-block',  // 가로로 나열되도록 설정
                      }}
                    >
                      <Typography align="center">{roomState[status]}</Typography>
                    </Box>
                  ))}
                </Grid>
              ))}
          </Grid>
        </Box>


      </Dialog>
    </React.Fragment>
  );
}

export default TablePage;
